mapper.select:

	select fur_no, fur_name, fur_price, fur_good_num, fur_repl_num, fur_brand_name, fur_info
	from FURNITURE, FURNITURE_BRAND
	where FURNITURE.FUR_BRAND_NO = FURNITURE_BRAND.FUR_BRAND_NO and FURNITURE.FUR_NO = #{fur_no}

select fur_no, fur_name, fur_price, fur_brand_name, fur_good_num, fur_repl_num from furniture f, furniture_brand fb where f.fur_brand_no = fb.fur_brand_no
and f.fur_no in ( select fur_no from furniture_room_kind frk, room_kind_define rkd where frk.ROOM_KIND_DEF_NO = rkd.ROOM_KIND_DEF_NO and rkd.ROOM_KIND_DEF_NAME = #{room_kind_def_name}
and fur_no in ( select fur_no from furniture_kind_define fkd, furniture_kind fk where fkd.FUR_KIND_DEF_NO = fk.FUR_KIND_DEF_NO and FUR_KIND_DEF_NAME = #{fur_kind_def_name} )
)
and f.fur_no in ( select fur_no from furniture_concept fc, concept c where fc.CONCEPT_NO = c.CONCEPT_NO and c.CONCEPT_NAME = #{concept_name}
and fur_no in ( select fur_no from furniture_kind_define fkd, furniture_kind fk where fkd.FUR_KIND_DEF_NO = fk.FUR_KIND_DEF_NO and FUR_KIND_DEF_NAME = #{fur_kind_def_name} )
)
order by fur_no desc
fur_no desc
fur_good_num desc
fur_repl_num desc
